import React, { Component } from "react";
import "./CardDot.css";
import SingleCard from "../Card/SingleCard";
var bg={
  background:"lightblue"
}
export class CardDot extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "#361C37",
      title: "First",
      link:
        "https://www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/auth.png",
      details: "Demo text"
    };
  }

  first() {
    this.setState({
      ...this.state,
      background: "#361C37",
      title: "First",
      link:
        "https://www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/auth.png",
      details: "Demo text"
    });
  }

  second() {
    this.setState({
      ...this.state,
      background: "#C6B023",
      title: "First",
      link:
        "//www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/storage.png",
      details: "Demo text"
    });
  }

  third() {
    this.setState({
      ...this.state,
      background: "#17718D",
      title: "First",
      link:
        "//www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/database.png",
      details: "Demo text"
    });
  }

  fourth() {
    this.setState({
      ...this.state,
      background: "rgb(212, 0, 255)",
      title: "First",
      link:
        "https://www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/auth.png",
      details: "Demo text"
    });
  }
  render() {
    return (
      <div className="container text-center" style={bg}>
      <h1>Changeable CardView</h1>
        <br /><br />
        <div className="form-group row">
          <div className="col-md-1" />
          <div className="col-md-1 justify-content-center align-self-center">
            <span
              class="dot dotOne"
              onMouseOver={() => this.first()}
              onClick={() => this.first()}
            />
            <span
              class="dot dotTwo"
              onMouseOver={() => this.second()}
              onClick={() => this.second()}
            />
            <span
              class="dot dotThree"
              onMouseOver={() => this.third()}
              onClick={() => this.third()}
            />
            <span
              class="dot dotFour"
              onMouseOver={() => this.fourth()}
              onClick={() => this.fourth()}
            />
          </div>
          <div className="col-md-3" />
          <div className="col-md-6">
            <SingleCard
              background={this.state.background}
              details={this.state.details}
              link={this.state.link}
              title={this.state.title}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default CardDot;
