import React, { Component } from "react";
import "./CardDot.css";
import SingleCard from "../Card/SingleCard";
import ChildPic from "./CHILD-SECURITY.png";
import IntercomPic from "./E-INTERCIM.png";
import VisitorlistPic from "./VISITORS-LIST.png";
import VMPic from "./VISITORS-MANAGMENT.png";

var back0 = "#23B6C9";


var bg = {
  // background: "#FFFFFF",
  // color: "lightblue"
};
export class CardDot extends Component {
  constructor(props) {
    super(props);
    this.state = {
      background: "#23B6C9",
      title: "What is Lorem Ipsum?",
      link:
        "//www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/storage.png",
      details:
        "simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's when an unknown printer took a galley of type"
    };
  }

  func(id) {
    let back = null,
      title = null,
      link = null;
    if (id == 0) {
      back = "#23B6C9";
      title = "What is Lorem Ipsum?";
      link =
        "simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's when an unknown printer took a galley of type";
    } else if (id == 1) {
      back = "#EBB206";
      title = "Why do we use it?";
      link = "//www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/database.png";
      this.setState({
        ...this.state,
        background: "#EBB206",
        title: "Why do we use it?",
        link:
          "//www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/database.png",
        details:
          "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout"
      });
    } else if (id == 2) {
      this.setState({
        ...this.state,
        background: "#AB60B8",
        title: "Where does it come from?",
        link:
          "//www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/auth.png",
        details:
          "Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old."
      });
    } else {
      this.setState({
        ...this.state,
        background: "#23B6C9",
        title: "Where can I get some?",
        link:
          "//www.gstatic.com/mobilesdk/160505_mobilesdk/discoverycards/2x/storage.png",
        details:
          "There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour"
      });
    }
  }

  LogoImage() {
    let logo = [];
    let imageId = [ChildPic, IntercomPic, VisitorlistPic, VMPic];
    for (let i = 0; i < 4; i++) {
      logo.push(
        <img
          id="logo"
          src={imageId[i]}
          onMouseOver={() => this.func(i)}
          onClick={() => this.func(i)}
        />
      );
    }

    return logo;
  }

  render() {
    return (
      <div style={bg}>
        <div className="container text-center">
          <br />
          <h2>Features</h2>
          <br />
          <div className="form-group row">
            <div className="col-md-1" />
            <div className="col-md-1 justify-content-center align-self-center">
              {this.LogoImage()}
            </div>
            <div className="col-md-2" />
            <div className="col-md-7">
              <SingleCard
                background={this.state.background}
                details={this.state.details}
                link={this.state.link}
                title={this.state.title}
              />
              <br />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default CardDot;
